// Theme configuration.
@import 'config/all';
@import 'mixins/fonts';
@import '../../../../../../../libraries/bootstrap/scss/bootstrap';
@import '../../../../../../../libraries/bootstrap/scss/bootstrap-grid';
@import '../../../../../../../libraries/bootstrap/scss/bootstrap-reboot';

// Fonts.
@import 'fonts/fontello';
@import '../../../../../../../libraries/lato-webfont/scss/lato-regular';

// Layout.
@import 'layout/global';

$grid-gutter-width: 60px !default;

$container-max-widths: (
  sm: 540px,
  md: 720px,
  lg: 960px,
  xl: 1200px
) !default;

$font-family-sans-serif: 'Lato', sans-serif !default;
$font-family-base:       'Lato', sans-serif !default;




//$font-size-base: 1rem !default; // Assumes the browser default, typically `16px`

$font-size-vlg: 1.375rem !default; //22px
//$font-size-lg:   1.25rem !default; // 20px
$font-size-mdlg: 1.125rem !default; //18px
$font-size-mdsm: 0.94rem !default; //15px
//$font-size-sm:   .875rem !default;  // 14px
$font-size-vsm: 0.8125rem !default; //13px
$font-size-svsm: 0.74rem !default; //13px

// --------------READ THIS -------------------
// Here we can stick in only general colour applicable to the whole site
// So only applicable to generic elements <html><body><p><ul> etc not restricted
// to a particular block, paragraph or section
// All other colours should be defined in a component they refer to and have own variables
// For example check _d_p_banner.scss

$link-color: $color-agamemnon !default;
$link-hover-color: darken($link-color, 5%) !default;

$white: #fff !default;
$black: #000 !default;


// -----------COLOUR NAMING CONVENTION ----------//

// The above are so obvious thet thay stay as they are. There is just one total black and one total white.

// A general situation on a website looks like this: There are 2-3 main sets of colours and there are accents.
//
// There is however not a single easy way to see which colours will end up where
// on which website or even or a particular section.
// Any naming convention is bound to fail. To devise a mechanism in which we do not end up with contradicting
// name and value of a color, we completely abstract variable names from colours they hold.
// In this way we hope we can build many websites on the same codebase without making a mess and perhaps with
// some additional fun.
//
// The apporach:
// We derive name the variables from the participants of the Troyan wors (The Greeks and the Troyans)
// Plenty of names can be found here: https://en.wikipedia.org/wiki/Trojan_War

// We then obviously have 2 sets of colors (one for the Troyans and one for Greeks). If you need a 3rd set,
// use greek gods
// Accents are named after those who stood out in the wars: Achillies, Hektor, Helen, Troyan-hors ;)

// I have no idea if this is a good idea but all the other ideas  failed immediately when I wanted to theme site
// number 2 which was brown/orange, when the first base was blue/grey. This was not even a case of dark vs bright
// but a completely different set of colours. Hence this small experiment.

// The Greeks
//Agamemnon  Menelaus Nestor Odysseus Diomedes Patroclus Thersites Achaeans Myrmidons

$color-agamemnon: #196dba !default;
$color-menelaus: #5d98ce !default;
$color-nestor: #65e2ff !default;
$color-odysseus: white !default;

// The Troyans
// Priam Hecuba Troilus Cassandra Andromache Aeneas Memnon Penthesilea and the Amazons Sarpedon
$color-priam: #7a7a7a !default;
$color-hecuba: #727272 !default;
$color-troilus: #4c4c4c !default;
$color-cassandra: black !default;

// The accents
// Achillies, Hector, Helen, Paris
$color-hector: #d3d3d3 !default;
$color-achillies: #ec6d06 !default;

// The Gods
// Caused the war: Eris Zeus
// On the Greek side: Athena Hera Hephaestus Hermes Poseidon Thetis
// On the Trojan side: Aphrodite Apollo Ares Artemis Leto Scamander

//$colour-zeus: #ff0000 !default; // just an example

// Colour variables
$font-button-color: $color-odysseus !default;

//@TODO Mojority of these mixins should really be distributed to the components they refer to.
// I know this might cause duplication to some extend but here we should only have generic ones
// (@font-footer-body is not too generic.
// Here we could rather have @h2 @h3 or @ul general theming

$font-main-header-size: 3rem;
$font-main-header-size-down: 2rem;
@mixin font-main-header {
  font-size: $font-main-header-size;
  font-weight: bold;
  text-align: center;
  @include media-breakpoint-down(md) {
    font-size: $font-main-header-size-down;
  }
}

$font-main-header-big-size: 3.44rem;
$font-main-header-big-size-down-md: 2.5rem;
$font-main-header-big-size-down-sm: 2.5rem;
@mixin font-main-header-big {
  text-transform: uppercase;
  font-weight: 900;
  margin-bottom: 0.8rem;
  text-align: center;
  font-size: $font-main-header-big-size;
  @include media-breakpoint-down(md) {
    font-size: $font-main-header-big-size-down-md;
  }
  @include media-breakpoint-down(sm) {
    font-size: $font-main-header-big-size-down-sm;
  }
}



@mixin font-subtitle {
  font-size: $font-size-mdlg;
  @include media-breakpoint-down(sm) {
    font-size: $font-size-sm;
  }
}

@mixin font-button {
  font-size: $font-size-mdsm;
  text-transform: uppercase;
  font-weight: bold;
  color: $font-button-color;
  @include media-breakpoint-down(sm) {
    font-size: $font-size-vsm;;
  }
  &:hover {
    text-decoration: none;
  }
}
@mixin font-body-standard {
  font-size: $font-size-base;
  @include media-breakpoint-down(md) {
    font-size: $font-size-mdsm;
  }
  @include media-breakpoint-down(sm) {
    font-size: $font-size-sm;
  }
}

@mixin font-links {
  font-size: $font-size-base;
}

@mixin text-transform($transform: none) {
  @if $transform == none {
    // we transform up only
    // if you have to transfer to none somewhere
    // then this is a but, please report
  } @else {
    text-transform: $transform;
  }
}

@import 'barrio/breadcrumb';
@import 'barrio/contextual';
@import 'barrio/feed-icon';
@import 'barrio/field';
@import 'barrio/form';
@import 'barrio/header';
@import 'barrio/help';
@import 'barrio/icons';
@import 'barrio/image-button';
@import 'barrio/item-list';
@import 'barrio/list-group';
@import 'barrio/node-preview';
@import 'barrio/node';
@import 'barrio/page';
@import 'barrio/search-form';
@import 'barrio/search-results';
@import 'barrio/shortcut';
@import 'barrio/site-footer';
@import 'barrio/skip-link';
@import 'barrio/table';
@import 'barrio/ui.widget';
@import 'barrio/tabs';
@import 'barrio/textarea';
@import 'barrio/user';
@import 'barrio/views';
@import 'barrio/ui-dialog';
@import 'components/decorations';
@import 'components/main-menu';
@import 'components/buttons.scss';
@import 'components/forms';

// Paragraphs
//
//
// Styleguide components.d_p
@import 'components/d_lang-dropdown';
@import 'components/d_p_subscribe';
@import 'components/d_p_banner';
@import 'components/d_p_side_image';
@import 'components/d_p_text_blocks';
@import 'components/d_p_text_paged';

// Colour variables used in this paragraph
$d-p-banner-text-color: $color-odysseus !default;
$d-p-banner-background-overlay: $color-cassandra !default;
$d-p-banner-subtitle-background: $color-odysseus !default;

.d-p-banner {
  background: $d-p-banner-background-overlay;
  color: $d-p-banner-text-color;
  z-index: 0;
  h2 {
    @include font-main-header-big;
  }
  h3 {
    @include font-subtitle;
    text-align: center;
    &:after {
      @include decoration-header-line-main-center;
      background: $d-p-banner-subtitle-background;
    }
  }
  p {
    @include font-body-standard;
    text-align: center;
    line-height: 1.7;
  }
  a {
    @extend .btn-orange;
    margin-top: 70px;
  }
  img {
    opacity: 0.5;
    @include media-breakpoint-down(sm) {
      max-width: none;
      width: 100%;
      overflow: hidden;
    }
  }
  .content-wrapper {
    width: 100%;
    padding-top: 18.5%;
    padding-bottom: 14%;
    @include media-breakpoint-down(sm) {
      padding-top: 100px;
      padding-bottom: 100px;
    }
  }
}

// Colour variables used in this paragraph
$d-p-subscribe-file-color: $color-odysseus !default;
$d-p-subscribe-file-background: $color-agamemnon !default;

.d-p-subscribe-file-form {
  margin-left: 11%;
  margin-top: 35px;
  margin-bottom: 20px;

  .form-control {
    border: none;
  }

  @include make-inline-form;
  fieldset.form-item-name {
    max-width: 210px;
    margin-right: 20px;
  }
  fieldset.form-item-mail {
    max-width: 350px;
    margin-right: 20px;
  }
  @include media-breakpoint-down(lg) {
    margin-left: 3%;
    fieldset.form-item-mail {
      max-width: 300px;
      margin-right: 20px;
    }
  }
  @include media-breakpoint-down(md) {
    max-width: 500px;
    margin: 3% auto;
    fieldset.form-item-name, fieldset.form-item-mail {
      max-width: 100%;
      margin-right: 0;
    }
    .button {
     // width: 100%;
    }
  }
}

.subscribe_file {
  color: $d-p-subscribe-file-color;
  z-index: 0;
  padding: 8% 0;
  background: $d-p-subscribe-file-background;
  img {
    opacity: 0.4;
  }
  h2 {
    @include font-main-header;
    &:after {
      @include decoration-header-line-main-center;
      margin-top: 23px;
    }
  }
  p {
    text-align: center;
  }
}

// Colour variables used in this paragraph
$d-p-single-box-text-color: $color-hecuba !default;
$d-p-text-blocks-underline-color: $color-agamemnon !default;
$d-p-text-blocks-background: $color-agamemnon !default;
$d-p-single-text-box-cta-text-transform: uppercase !default;


@mixin font-title-single-box {
  font-size: $font-size-lg;
  font-weight: bold;
  text-align: left;
  @include media-breakpoint-down(sm) {
    font-size: $font-size-mdlg;
  }
}




.d-p-group-of-text-blocks {
  & > h2 {
    @include font-main-header;
    margin: 124px 0 88px;
    @include media-breakpoint-down(md) {
      margin: 65px 0 45px;
    }
    &:after {
      @include decoration-header-line-main-center;
      background: $d-p-text-blocks-underline-color;
      margin: 10px auto;
    }
  }
  > .field--type-link a {
    @extend .btn-orange;
    margin: 50px auto 145px;
    @include media-breakpoint-down(md) {
      margin: 30px auto 70px;
    }
  }
}

.single-box-wrapper {
  @include media-breakpoint-up(lg) {
      &:nth-child(3n+2) {
        padding-left: 60px;
      }
      &:nth-child(3n+3) {
        padding-left: 80px;
      }
  }
}
.d-p-text-block {
  margin-bottom: 15%;
  h2 {
    @include font-title-single-box;
    &:after {
      @include decoration-header-line;
      background: $d-p-text-blocks-background;
    }
  }
  p {
    @include font-body-standard;
    color: $d-p-single-box-text-color;
  }
  a {
    @include text-transform($d-p-single-text-box-cta-text-transform)

  }
}

// Colour variables used in this paragraph
$d-p-blog-subtitle-text-color: $color-agamemnon !default;
$d-p-blog-subtitle-line-background: $color-agamemnon !default;
$d-p-blog-text-color: $color-hecuba !default;

.text-page {
  h2 {
    margin-top: 5%;
  }
  h3 {
    margin: 18px 0;
    @include font-subtitle;
    text-align: center;
    color: $d-p-blog-subtitle-text-color;
    &:after {
      @include decoration-header-line-main-center;
      background: $d-p-blog-subtitle-line-background;
      margin: 15px auto;
    }
  }
  p {
    color: $d-p-blog-text-color;
  }
  margin-top: 5%;
  margin-bottom: 5%;
}

// Colour variables used in this component.
$header-line-background: $color-nestor !default;
$header-line-main-background: $color-nestor !default;

@mixin decoration-header-line {
    content: ' ';
    display: block;
    width: 50px;
    background: $header-line-background;
    position: relative;
    height: 2px;
    margin-top: 10px;
    margin-bottom: 20px;
}

@mixin decoration-header-line-main-center {
    content: ' ';
    margin: 22px auto 24px auto;
    display: block;
    width: 50px;
    background: $header-line-main-background;
    position: relative;
    height: 2px;
}

// Colour variables used used in this component.
$form-control-border: $color-hecuba !default;

.form-control {
  padding: 15px 1.5rem 15px;;
  border: 1px solid $form-control-border;
  border-radius: 0;
}

.form-text {
  display: block;
  margin-top: 0.25rem;
}

.form-email {
  display: block;
  margin-top: 0.25rem;
}

@mixin make-inline-form {
  fieldset {
    display: inline-block;
    input {
      margin-top: 0;
    }
  }
  @include media-breakpoint-down(md) {
    display: block;
  }
  .button {
    display: inline-block;
    vertical-align: top;
    @include media-breakpoint-down(md) {
      display: block;
      margin: 0 auto;
    }
    @include media-breakpoint-down(xs) {
      width: 100%;
    }
  }
}

// Colour variables used in this paragraph
$d-p-side-image-banner-background-color: $color-agamemnon !default;
$d-p-side-image-banner-background-color-secondary: darken($d-p-side-image-banner-background-color, 15%) !default;
$d-p-side-image-banner-text-color: $color-odysseus !default;
$d-p-side-image-banner-link-color: $color-nestor !default;
$d-p-side-image-banner-link-hover-color: darken($d-p-side-image-banner-link-color, 5%) !default;

.d-p-side-image-banner {
  color: $d-p-side-image-banner-text-color;
  h2 {
    @include font-main-header;
    text-align: left;
    &:after {
      @include decoration-header-line;
      margin-top: 23px;
    }
  }
  a {
    color: $d-p-side-image-banner-link-color;
    &:hover {
      color: $d-p-side-image-banner-link-hover-color;
    }
  }
  .d-image {
    padding: 0;
    @include media-breakpoint-down(sm) {
      height: 300px !important;
    }
  }
  .container-half {
    padding: 7%;
  }
  .image-side {
    &-right {
      background: $d-p-side-image-banner-background-color;
    }
    &-left {
      background: $d-p-side-image-banner-background-color-secondary;
    }
  }

  @include media-breakpoint-up(md) {
    .container-half {
      margin-right: auto;
      margin-left: auto;
      padding-right: ($grid-gutter-width / 2);
      padding-left: ($grid-gutter-width / 2);
      width: 570px;
    }
    .image-side {
      &-right {
        .d-image {
          float: right;
        }
        .container-half {
          padding-right: 5%;
          float: right;
        }
      }
      &-left {
        .d-image {
          float: left;
        }
        .container-half {
          padding-left: 5%;
          float: left;
        }
      }
    }
  }
}

// Colour variables used in this component.
$lang-dropdown-header-color: $color-priam !default;
$lang-dropdown-toggle-color: $color-hector !default;
$lang-dropdown-link-color: $menu-main-link !default;
$lang-dropdown-link-hover-color: $menu-main-link-hover !default;
$lang-dropdown-link-active-color: $menu-main-link-active !default;
$lang-dropdown-mobile-background: $color-troilus !default;
$lang-dropdown-mobile-link-color: $color-priam !default;
$lang-dropdown-mobile-link-hover-color: lighten($lang-dropdown-mobile-link-color, 5%) !default;
$lang-dropdown-mobile-header-color: $color-odysseus !default;

@mixin font-lang-switcher-links {
  font-size: $font-size-mdsm;
  color: $lang-dropdown-link-color;
  text-transform: uppercase;
  &:hover, &:focus {
    color: $lang-dropdown-link-hover-color;
  }
  &:active {
    color: $lang-dropdown-link-active-color;
  }
}

.block-language {
  color: $lang-dropdown-header-color;
  @include media-breakpoint-up(lg) {
    &.dropdown {
      width: 135px;
      .dropdown-toggle {
        text-transform: uppercase;
        text-align: right;
        vertical-align: middle;
        cursor: pointer;
        padding-right: 30px;
        font-size: 20px;
        color: $lang-dropdown-header-color;
        &:after {
          color: $lang-dropdown-toggle-color;
          font-family: "fontello";
          display: inline-block;
          width: 0;
          content: "\e804";
          border-top: none;
          border-right: none;
          border-left: none;
          font-size:40px;
          right: 15px;
          float: right;
          top: -16px;
          position: absolute;
        }
        &-down {
          &:after {
            content: "\e806";
          }
        }
      }
      .dropdown-menu {
        cursor: pointer;
        padding: 0;
        left: -57px;
        top: -10px;
        li {
          &:first-child, a {
            text-transform: uppercase;
            padding: 10px 120px 10px 25px;
            display: block;
          }
          &:nth-child(1):after {
            content: "\e806";
            color: $lang-dropdown-toggle-color;
            font-family: "fontello";
            display: inline-block;
            width: 0;
            border-top: none;
            border-right: none;
            border-left: none;
            font-size: 40px;
            right: 35px;
            float: right;
            top: -10px;
            position: absolute;
          }
          a {
            @include font-lang-switcher-links;
          }
        }
      }
    }
  }
  @include media-breakpoint-down(md) {
    .active-lang-code {
      display: none;
    }
    .dropdown-menu {
      position: relative;
      top: 100%;
      left: 0;
      z-index: 200;
      display: block;
      float: none;
      min-width: 10rem;
      padding: 20px;
      margin: 0;
      text-align: center;
      width: 100%;
      background-color: $lang-dropdown-mobile-background;
      border: 0;
      border-radius: 0;
      li {
        text-transform: uppercase;
        &:first-child {
          color: $lang-dropdown-mobile-header-color;
          padding: 10px;
        }
        a {
          display: block;
          color: $lang-dropdown-mobile-link-color;
          padding: 10px;
          &:hover {
            color: $lang-dropdown-mobile-link-hover-color;
          }
        }
      }
    }
  }
}

// Colour variables used in this component.
$button-main-background: $color-achillies !default;
$button-main-border: darken($button-main-background, 5%) !default;
$button-main-hover-background: darken($button-main-background, 5%) !default;
$button-main-hover-border: darken($button-main-background, 5%) !default;
$button-main-hover-shadow: $button-main-background !default;

.btn {
  &-primary,
  &-orange {
    background: $button-main-background;
    @include font-button;
    min-width: 296px;
    display: table;
    cursor: pointer;
    padding: 16px;
    margin: 0 auto;
    text-align: center;
    letter-spacing: 1.3px;
    border-radius: 0;
    border-color: $button-main-border;
    &:hover, &:focus  {
      background: $button-main-hover-background;
      border-color: $button-main-hover-border;
      box-shadow: 0 0 0 2px $button-main-hover-shadow;
    }
    @include media-breakpoint-down(sm) {
      min-width: 260px;
      padding: 14px;
    }
  }
}

// Colour variables used in this component
$menu-main-link: $color-priam !default;
$menu-main-link-hover: darken($color-priam, 20%) !default;
$menu-main-link-active: darken($color-priam, 20%) !default;
$menu-main-link-dropdown: $color-hector !default;

$menu-mobile-background: $color-agamemnon !default;
$menu-mobile-link-color: $color-odysseus !default;
$menu-mobile-link-border: $color-menelaus !default;
$menu-mobile-link-hover-background: $color-hector !default;

$navbar-toggler-background: $color-agamemnon !default;
$navbar-toggler-color: $color-agamemnon !default;

@mixin font-main-menu-links {
  font-size: $font-size-lg;
  text-transform: capitalize;
}


.main-menu {
  .menu-item--expanded {
    a.nav-link:after {
      color: $menu-main-link-dropdown;
      font-family: "fontello";
      display: inline-block;
      width: 0;
      content: "\e804";
      font-size: 19px;
      right: -5px;
      top: 2px;
      position: relative;
    }
    &.show {
      a.nav-link:after {
        content: "\e806";
      }
    }
    .dropdown-menu {
      border: 0;
    }
  }
  @include media-breakpoint-up(lg) {
    &.navbar-nav {
      .nav-link {
        @include font-main-menu-links;
        color: $menu-main-link;
        &:hover, &:focus {
          color: $menu-main-link-hover;
        }
        &:active {
          color: $menu-main-link-active;
        }
        line-height: 4;
        padding-left: 45px;
      }
      .dropdown-menu {
        .dropdown-item {
          padding: 0;
          color: $menu-mobile-link-color;
          &:active, &:hover {
            background: $menu-mobile-link-hover-background;
            a {
              color: $menu-mobile-link-color;
            }
          }
          a {
            color: $menu-main-link;
            padding: 5px 20px;
            width: 100%;
            display: block;
          }
        }
      }
    }
  }
  @include media-breakpoint-down(md) {
    &.navbar-nav {
      background: $menu-mobile-background;
      color: $menu-mobile-link-color;
      .nav-link {
        @include font-main-menu-links;
        color: $menu-mobile-link-color;
        width: 80%;
        border-bottom: 2px solid $menu-mobile-link-border;
        margin: 0 auto;
        padding: 15px;
        &:hover, &:focus {
          color: darken($menu-mobile-link-color, 5%);
        }
        &:active {
          color: darken($menu-mobile-link-color, 5%);
        }
        line-height: 1;
        text-align: center;
      }
      .menu-item--expanded {
        a.nav-link {
          @include font-main-menu-links;
          &:after {
            right: 0;
            top: 2px;
            float: right;
          }

        }
      }
      .nav-item:last-child {
